// PWABuilder Service Worker
// This service worker is generated by PWABuilder and is optimized for PWA functionality

const CACHE_NAME = 'zentrio-pwa-v9';
const RUNTIME_CACHE = 'zentrio-runtime-v9';

// URLs to cache during install
const STATIC_CACHE_URLS = [
  '/manifest.json',
  '/css/styles.css',
  '/css/background.css',
  '/icons/icon-192.png',
  '/icons/icon-512.png',
  '/screenshots/mobile-1.jpg',
  '/screenshots/mobile-2.jpg',
  '/screenshots/mobile-3.jpg'
];

importScripts('/service-worker-logic.js');

// Install event - cache essential resources
self.addEventListener('install', event => {
  event.waitUntil(
    caches.open(CACHE_NAME)
      .then(cache => {
        return cache.addAll(STATIC_CACHE_URLS);
      })
      .then(() => {
        return self.skipWaiting();
      })
  );
});

// Activate event - clean up old caches
self.addEventListener('activate', event => {
  event.waitUntil(
    caches.keys().then(cacheNames => {
      return Promise.all(
        cacheNames.map(cacheName => {
          if (cacheName !== CACHE_NAME && cacheName !== RUNTIME_CACHE) {
            return caches.delete(cacheName);
          }
        })
      );
    }).then(() => {
      return self.clients.claim();
    }).then(() => {
        processDownloadQueue();
        return Promise.resolve();
    })
  );
});

// Fetch event - serve from cache, fallback to network
self.addEventListener('fetch', event => {
  const _url = new URL(event.request.url);

  // Bypass caching for development environment
  if (self.location.hostname === 'localhost') {
    event.respondWith(fetch(event.request));
    return;
  }


  // Only handle requests for static assets
  const isStaticAsset = STATIC_CACHE_URLS.some(assetUrl => event.request.url.endsWith(assetUrl));
  if (!isStaticAsset) {
    return;
  }

  event.respondWith(
    caches.match(event.request).then(cachedResponse => {
      if (cachedResponse) {
        return cachedResponse;
      }

      // Create a new request object that can handle redirects
      return fetch(event.request).then(response => {
        // Do not cache redirected responses.
        if (response.redirected) {
          return response;
        }

        // Do not cache responses that are not successful.
        if (!response || response.status !== 200 || response.type !== 'basic') {
          return response;
        }

        // Clone the response for caching
        const responseToCache = response.clone();

        caches.open(RUNTIME_CACHE).then(cache => {
          cache.put(event.request, responseToCache);
        });

        return response;
      }).catch(() => {
        // Return offline fallback for navigation requests
        if (event.request.mode === 'navigate') {
          return caches.match('/');
        }
        
        // Return a basic offline response for other requests
        return new Response('Offline', {
          status: 408,
          statusText: 'Offline'
        });
      });
    })
  );
});

// Background sync for when the app comes back online
self.addEventListener('sync', event => {
  if (event.tag === 'background-sync') {
    event.waitUntil(
        processDownloadQueue()
    );
  }
});

// Push notifications (if needed)
self.addEventListener('push', event => {
  if (event.data) {
    const data = event.data.json();
    
    event.waitUntil(
      self.registration.showNotification(data.title, {
        body: data.body,
        icon: '/icons/icon-192.png',
        badge: '/icons/icon-192.png',
        tag: 'zentrio-notification'
      })
    );
  }
});

// Notification click handler
self.addEventListener('notificationclick', event => {
  event.notification.close();
  
  event.waitUntil(
    clients.openWindow('/')
  );
});
